import { Link } from 'react-router-dom';
import { useContext, useState } from 'react';

import classes from './DoughnutItem.module.css'
import CartContext from '../../../store/cart-context';
import DoughnutItemForm from './DoughnutItemForm'

const DoughnutItem = props => {
    const cartCtx = useContext(CartContext);

    const price = `$${props.price.toFixed(2)}`;

    const addToCartHandler = amount => {
        cartCtx.addItem({
            id: props.id,
            image: props.image,
            name: props.name,
            amount: amount,
            price: props.price
        })
    }

    return (<li className={classes.doughnut}>
        <div>
            <Link to={`/doughnuts/${props.id}`}><img srcSet={props.image} alt={props.name} /></Link>
            <h3>{props.name}</h3>
            <div className={classes.bottom}>
                <div className={classes.price}>{price}</div>
                <DoughnutItemForm onAddToCart={addToCartHandler} />
            </div>
        </div>

    </li>)
}

export default DoughnutItem;
